import java.io.BufferedWriter;
import java.io.IOException;

public class BDR extends plm.universe.bugglequest.AbstractBuggle {

	@Override
	public void command(String command, BufferedWriter out) {
		int num = Integer.parseInt((String) command.subSequence(0, 3));
		try {
			switch(num){
			case 148:
				out.write(getIndication());
				out.write("\n");
				out.flush();
				break;
			default:
				super.command(command, out);
				break;
			}
		}catch (IOException ioe) {
			ioe.printStackTrace();
		}
	}

	public char getIndication() { 
		if (isOverMessage()) { 
			return readMessage().charAt(0); 
		} else { 
			return ' '; 
		} 
	}

	public void run() {
		/* BEGIN SOLUTION */
		while (true) {
			char c = getIndication();

			if (c == 'R') { 
				right(); forward();
			} else if (c == 'L') {
				left(); forward();
			} else if (c == 'I') {
				back(); forward(); 
			} else if (c == 'A')
				forward();
			else if (c == 'B')
				forward(2);  
			else if (c == 'C')
				forward(3);
			else if (c == 'Z')
				backward();
			else if (c == 'Y')
				backward(2);  
			else if (c == 'X')
				backward(3);
			else 
				return ;
		}		
		/* END SOLUTION */
	}
}

