import plm.core.model.I18nManager;
import plm.universe.bugglequest.AbstractBuggle;

public class TraversalDiagonal extends AbstractBuggle {
	/* BEGIN TEMPLATE */
	int diag = 0;
	public void run() {
		/* BEGIN SOLUTION */
		int cpt = 0;
		writeMessage(cpt);
		while (!endingPosition()) {
			nextStep();
			cpt++;
			writeMessage(cpt);
		}
	}

	public void nextStep() {
		int x = getX();
		int y = getY();

		if ((x + 1 < getWorldWidth()) && (y > 0)) {
			x++;
			y--;
		} else if (diag + 1 < getWorldHeight()) {
			diag++;
			y = diag;
			x = 0;
		} else {
			diag++;
			x = diag - (getWorldWidth() - 1);
			y = diag - x;
		}

		setPos(x, y);
	}

